func Sum(fileName string) (ret int64, _ error) {
   b, err := os.ReadFile(fileName) ![1](assets/1.png)
   if err != nil {
      return 0, err
   }

   for _, line := range bytes.Split(b, []byte("\n")) { ![2](assets/2.png)
      num, err := strconv.ParseInt(string(line), 10, 64) ![3](assets/3.png)
      if err != nil {
         return 0, err
      }

      ret += num
   }

   return ret, nil
}
